// NotQuiteLisp1 returns the answer for day 1, part 1
func NotQuiteLisp1() int {
	floor := 0
	for _, char := range input {
		switch char {
		case '(':
			floor++
		case ')':
			floor--
		}
	}

	return floor
}

// NotQuiteLisp2 returns the answer for day 1, part 2
func NotQuiteLisp2() int {
	floor := 0

	for i, char := range input {
		switch char {
		case '(':
			floor++
		case ')':
			floor--
		}

		if floor < 0 {
			return i + 1
		}
	}
	// Hopefully their input gets us negative at some point
	return -1
}
